<?php/** * The main template file. * * This is the most generic template file in a WordPress theme * and one of the two required files for a theme (the other being style.css). * It is used to display a page when nothing more specific matches a query. * For example, it puts together the home page when no home.php file exists. * * Learn more: http://codex.wordpress.org/Template_Hierarchy * * * @package WordPress * @subpackage SocialChef * @since SocialChef 1.0 */get_header('buddypress'); SocialChef_Theme_Utils::breadcrumbs();get_sidebar('under-header');?>	<div class="row">		<?php  if ( have_posts() ) : ?>		<header class="s-title">			<h1><?php echo sprintf(__('%s Blog', 'socialchef'), get_bloginfo('name')); ?></h1>		</header>		<section class="content three-fourth">			<?php			while (have_posts()) : the_post(); ?>						<!--blog post-->			<article id="post-<?php the_ID(); ?>" <?php post_class( ); ?>>				<div class="entry-meta">					<div class="date">						<span class="day"><?php echo get_the_date('d') ?></span> 						<span class="my"><?php echo get_the_date('M') ?>, <?php echo get_the_date('Y') ?></span>					</div>					<div class="avatar">						<a href="<?php echo esc_url( get_author_posts_url(get_the_author_meta( 'ID' )) ) ?>"><?php echo get_avatar( get_the_author_meta( 'ID' ), 32 ); ?><span><?php echo get_the_author() ?></span></a>					</div>				</div>				<div class="container">					<?php if ( has_post_thumbnail() ) { ?>					<div class="entry-featured"><a href="<?php the_permalink() ?>"><?php the_post_thumbnail('featured', array('title' => get_the_title())); ?></a></div>					<?php } ?>					<div class="entry-content">						<h2><a href="<?php the_permalink() ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a></h2>						<?php the_excerpt(); ?>					</div>					<div class="actions">						<div>							<div class="category"><i class="ico i-category"></i><?php the_category(' ') ?></div>							<div class="comments"><i class="ico i-comments"></i><a href="<?php comments_link(); ?>"><?php comments_number('0','1','%'); ?></a></div>							<div class="leave_comment"><a href="<?php comments_link(); ?>"><?php _e('Leave a comment', 'socialchef'); ?></a></div>							<div class="more"><a href="<?php the_permalink() ?>"><?php _e('Read more', 'socialchef'); ?></a></div>						</div>					</div>				</div>			</article>			<!--//blog post-->			<?php endwhile; ?>			<div class="pager">				<?php 				global $wp_query;				SocialChef_Theme_Utils::display_pager($wp_query->max_num_pages); ?>			</div>		</section>		<?php endif; ?>		<?php get_sidebar('right'); ?>	</div><!--//row--><?php 	get_footer( 'buddypress' );